create table tbl_timesheets
(
    id                      int auto_increment
        primary key,
    code                    varchar(50) null,
    user_id                 int         null,
    number_shifts_work      int         null comment 'Số ca đi làm',
    number_shifts_late_work int         null comment 'Số ca đi muộn',
    month                   int         null,
    created_at              bigint      null,
    updated_at              bigint      null,
    created_by              int         null,
    updated_by              int         null,
    deleted_at              bigint      null,
    deleted_by              int         null,
    status                  int         null,
    constraint fk3_user_id
        foreign key (user_id) references tbl_users (id)
            on update cascade on delete cascade
)
    collate = utf8mb4_bin;

create index fk3_user_id_idx
    on tbl_timesheets (user_id);

